definition.script:
pipeline {
    agent { label 'deb' }
    stages {
       stage('CheckOut') {
          steps {
             sh '''
                git clone https://github.com/subutai-io/agent && cd agent && git checkout --track origin/no-snap && rm -rf .git* && cd .. 
                git clone https://github.com/happyaron/subutai-agent && cp -r subutai-agent/debian/ agent/

             '''
           }
       }
        stage('Tweaks') {
            steps {
                sh '''
                    VER=6.4.12+$(date +%Y%m%d%H%M%S)
                    cd agent && sed -i "s/quit/native/" debian/source/format
                    dch -v $VER -D stable "Test build for $VER" 1>/dev/null 2>/dev/null
                '''
                sh 'ls -lt'
            }
        }
    }
}
