definition.script:
node (){
	String debFileName = "/tmp/jenkins/tempfile"

	String url = "https://eu0.cdn.subut.ai:8338/kurjun/rest"
	String user = "jenkins"

	def authID = sh (script: """
		set +x
		curl -s -k ${url}/auth/token?user=${user} | gpg --clearsign --no-tty
		""", returnStdout: true)
	def token = sh (script: """
		set +x
		curl -s -k -Fmessage=\"${authID}\" -Fuser=${user} ${url}/auth/token
		""", returnStdout: true)

	String responseDeb = sh (script: "curl -s -k https://eu0.cdn.subut.ai:8338/kurjun/rest/raw/info?name=tmpfile", returnStdout: true)
	sh "curl -s -k -Ffile=@${debFileName} -Ftoken=${token} ${url}/raw/upload"

	if (responseDeb != "Not found") {
		def jsonDeb = jsonParse(responseDeb)	
		sh "curl -s -k -X DELETE ${url}/apt/delete?id=${jsonDeb["id"]}'&'token=${token}"
	}
}

@NonCPS
def jsonParse(def json) {
    new groovy.json.JsonSlurperClassic().parseText(json)
}
