definition.script:
node(){
    String debFileName = "management-dev.deb"
    url = "https://devcdn.subut.ai:8338/kurjun/rest"
	String responseDeb = sh (script: """
			curl -s -k ${url}/apt/info?name=${debFileName}
			""", returnStdout: true)
	def jsonDeb = jsonParse(responseDeb)
	sh """
				echo /apt/delete?id=${jsonDeb["id"]}'&'token=
		"""
}

@NonCPS
def jsonParse(def json) {
    new groovy.json.JsonSlurperClassic().parseText(json)
}
