definition.script:
#!groovy
node() {
	agentVersion = sh (script: """
	        set +x
			version='1.0.0'
			[[ "\$version" =~ (.*)([0-9]+)([^0-9]*)\$ ]] && version="\${BASH_REMATCH[1]}\$((\${BASH_REMATCH[2]} + 1))\${BASH_REMATCH[3]}";
			echo \$version
			""", returnStdout: true).trim()
	println(agentVersion)
}
