definition.script:
node(){
    String url = "https://eu0.cdn.subut.ai:8338/kurjun/rest"
	String user = "jenkins"
	def authID = sh (script: "curl -s -k ${url}/auth/token?user=${user} | gpg --clearsign --no-tty", returnStdout: true)
	def token = sh (script: "curl -s -k -Fmessage=\"${authID}\" -Fuser=${user} ${url}/auth/token", returnStdout: true)

    String filename = "/tmp/uploadfromjenkins"
    def signature = sh (script: "curl -s -k -Ffile=@${filename} -Ftoken=${token} ${url}/template/upload | gpg --clearsign --no-tty", returnStdout: true)
    sh "curl -s -k -Ftoken=${token} -Fsignature=\"${signature}\" ${url}/auth/sign"
}
